  ListItemText,
  ListItemIcon,
  Box,
  Chip,
  Divider,
} from '@mui/material';
import ReceiptIcon from '@mui/icons-material/Receipt';
import AttachMoneyIcon from '@mui/icons-material/AttachMoney';
import { green, orange, red } from '@mui/material/colors';

interface Transaction {
  id: string;
  name: string;
  description: string;
  amount: string;
  date: string;
  status: 'paid' | 'pending' | 'overdue';
}

interface RecentTransactionsCardProps {
  title?: string;
  transactions: Transaction[];
}

const getStatusColor = (status: string) => {
  switch (status) {
    case 'paid':
      return green[500];
    case 'pending':
      return orange[500];
    case 'overdue':
      return red[500];
    default:
      return 'default';
  }
};

const getStatusLabel = (status: string) => {
  switch (status) {
    case 'paid':
      return 'Lunas';
    case 'pending':
      return 'Pending';
    case 'overdue':
      return 'Terlambat';
    default:
      return status;
  }
};

const RecentTransactionsCard: React.FC<RecentTransactionsCardProps> = ({
  title = 'Transaksi Terbaru',
  transactions,
}) => {
  return (
    <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
      <CardHeader
        avatar={<ReceiptIcon color="primary" />}
        title={title}
        sx={{ pb: 1 }}
      />
      <CardContent sx={{ flex: 1, pb: 1 }}>
        <List dense>
          {transactions.slice(0, 5).map((transaction) => (
            <React.Fragment key={transaction.id}>
              <ListItem alignItems="flex-start" sx={{ py: 1 }}>
                <ListItemIcon sx={{ minWidth: '32px', color: getStatusColor(transaction.status) }}>
                  <AttachMoneyIcon fontSize="small" />
                </ListItemIcon>
                <ListItemText
                  primary={
                    <Typography variant="body2" fontWeight={500}>
                      {transaction.name}
                    </Typography>
                  }
                  secondary={
                    <Box display="flex" flexDirection="column">
                      <Typography variant="caption" color="text.secondary">
                        {transaction.description}
                      </Typography>
                      <Box display="flex" justifyContent="space-between" alignItems="center" mt={0.5}>
                        <Typography variant="subtitle2" color="primary">
                          {transaction.amount}
                        </Typography>
                        <Chip
                          label={getStatusLabel(transaction.status)}
                          size="small"
                          variant="outlined"
                          sx={{
                            fontSize: '0.7rem',
                            height: '20px',
                            color: getStatusColor(transaction.status),
                            borderColor: getStatusColor(transaction.status),
                          }}
                        />
                      </Box>
                    </Box>
                  }
                />
              </ListItem>
              <Divider variant="middle" component="li" />
            </React.Fragment>
          ))}
        </List>
      </CardContent>
    </Card>
  );
};

export default RecentTransactionsCard;"}

