  Box,
  LinearProgress,
  Stack,
} from "@mui/material";
import MeetingRoomIcon from "@mui/icons-material/MeetingRoom";

interface OccupancyRateCardProps {
  title?: string;
  occupiedUnits: number;
  totalUnits: number;
  occupancyPercentage: number;
}

const OccupancyRateCard: React.FC<OccupancyRateCardProps> = ({
  title = "Tingkat Okupansi",
  occupiedUnits,
  totalUnits,
  occupancyPercentage,
}) => {
  const availableUnits = totalUnits - occupiedUnits;

  return (
    <Card sx={{ height: "100%", display: "flex", flexDirection: "column" }}>
      <CardHeader
        avatar={<MeetingRoomIcon color="primary" />}
        title={title}
        sx={{ pb: 1 }}
      />
      <CardContent sx={{ flex: 1 }}>
        <Stack spacing={2}>
          <Box>
            <Typography variant="h4" fontWeight={700} color="primary">
              {occupancyPercentage}%
            </Typography>
            <Typography variant="caption" color="text.secondary">
              Tingkat okupansi keseluruhan
            </Typography>
          </Box>

          <Box>
            <LinearProgress
              variant="determinate"
              value={occupancyPercentage}
              sx={{
                height: 10,
                borderRadius: 5,
                "& .MuiLinearProgress-bar": {
                  borderRadius: 5,
                },
              }}
            />
            <Box display="flex" justifyContent="space-between" mt={1}>
              <Typography variant="caption" color="text.secondary">
                {occupiedUnits} Unit Terisi
              </Typography>
              <Typography variant="caption" color="text.secondary">
                {availableUnits} Tersedia
              </Typography>
            </Box>
          </Box>

          <Stack
            direction="row"
            justifyContent="space-between"
            spacing={2}
            mt={1}
          >
            <Box textAlign="center">
              <Typography variant="h6" fontWeight={600}>
                {occupiedUnits}
              </Typography>
              <Typography variant="caption" color="text.secondary">
                Terisi
              </Typography>
            </Box>
            <Box textAlign="center">
              <Typography variant="h6" fontWeight={600}>
                {availableUnits}
              </Typography>
              <Typography variant="caption" color="text.secondary">
                Kosong
              </Typography>
            </Box>
            <Box textAlign="center">
              <Typography variant="h6" fontWeight={600}>
                {totalUnits}
              </Typography>
              <Typography variant="caption" color="text.secondary">
                Total
              </Typography>
            </Box>
          </Stack>
        </Stack>
      </CardContent>
    </Card>
  );
};

