  const getOccupancyColor = () => {
    if (occupancyPercentage >= 90) return green[500];
    if (occupancyPercentage >= 70) return orange[500];
    return red[500];
  };

  // Membuat ring progress dengan SVG
  const size = 120;
  const strokeWidth = 8;
