          <Stack
            direction="row"
            justifyContent="space-between"
            spacing={2}
            mt={1}
          >
            <Box textAlign="center">
              <Typography variant="h6" fontWeight={600}>
                {occupiedUnits}
              </Typography>
              <Typography variant="caption" color="text.secondary">
                Terisi
              </Typography>
            </Box>
            <Box textAlign="center">
              <Typography variant="h6" fontWeight={600}>
                {availableUnits}
              </Typography>
              <Typography variant="caption" color="text.secondary">
                Kosong
              </Typography>
            </Box>
            <Box textAlign="center">
              <Typography variant="h6" fontWeight={600}>
                {totalUnits}
              </Typography>
              <Typography variant="caption" color="text.secondary">
                Total
              </Typography>
            </Box>
          </Stack>
        </Stack>
      </CardContent>
    </Card>
  );
};

