import { Paper, Stack, Typography, Box } from "@mui/material";
import AddIcon from "@mui/icons-material/Add";

function AddDataCard() {
  return (
    <Paper
      elevation={0}
      sx={{
        p: 3,
        height: "100%",
        borderRadius: 5,
        border: "2px dashed",
        borderColor: "divider",
        bgcolor: "background.paper",
        cursor: "pointer",
        transition: "0.25s",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        "&:hover": {
          borderColor: "primary.main",
          bgcolor: "action.hover",
        },
      }}
    >
      <Stack alignItems="center" spacing={1.2}>
        <Box
          sx={{
            width: 48,
            height: 48,
            borderRadius: 3,
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            color: "primary.main",
            bgcolor: "rgba(0, 123, 255, 0.08)",
            transition: "0.2s",
          }}
        >
          <AddIcon fontSize="medium" />
        </Box>

        <Typography variant="subtitle1" fontWeight={600} color="text.primary">
          Add Data
        </Typography>
      </Stack>
    </Paper>
  );
}

export default AddDataCard;

"use client";
import { useState } from "react";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
} from "recharts";
import { Box, Paper, Typography, Button, Stack } from "@mui/material";

// Format angka ke Rupiah
const formatRupiah = (value: number) => {
  return new Intl.NumberFormat("id-ID", {
    notation: "compact",
    compactDisplay: "short",
  }).format(value);
};

interface TooltipPayload {
  name: string;
  value: number;
  stroke: string;
  // payload?: any; // Uncomment jika butuh akses data asli (income/expense objects)
}

interface CustomTooltipProps {
  active?: boolean;
  payload?: TooltipPayload[];
  label?: string;
}

// Tooltip custom saat hover di chart
function CustomTooltip({ active, payload, label }: CustomTooltipProps) {
  if (!active || !payload || payload.length === 0) return null;

  return (
    <Paper
      elevation={3}
      sx={{
        p: 2,
        backgroundColor: "white",
        border: "1px solid #e5e7eb",
        minWidth: 160,
      }}
    >
      <Typography variant="body2" color="text.secondary" mb={1}>
        {label}
      </Typography>
      {payload.map((item, index) => (
        <Box
          key={index}
          display="flex"
          justifyContent="space-between"
          alignItems="center"
          gap={2}
          mb={0.5}
        >
          <Box display="flex" alignItems="center" gap={1}>
            <Box
              sx={{
                width: 10,
                height: 10,
                borderRadius: "50%",
                bgcolor: item.stroke,
              }}
            />
            <Typography variant="body2" fontWeight={500}>
              {item.name}
            </Typography>
          </Box>
          <Typography variant="body2" fontWeight={700}>
            {formatRupiah(item.value)}
          </Typography>
        </Box>
      ))}
    </Paper>
  );
}

// Data dummy - mingguan
const dataMingguan = [
  { label: "Sen", income: 5500000, expense: 3200000 },
  { label: "Sel", income: 7300000, expense: 4100000 },
  { label: "Rab", income: 6400000, expense: 3800000 },
  { label: "Kam", income: 8900000, expense: 5100000 },
  { label: "Jum", income: 12500000, expense: 7200000 },
  { label: "Sab", income: 14100000, expense: 8800000 },
  { label: "Min", income: 10800000, expense: 6500000 },
];

// Data dummy - bulanan
const dataBulanan = [
  { label: "Jan", income: 45000000, expense: 24000000 },
  { label: "Feb", income: 38000000, expense: 28000000 },
  { label: "Mar", income: 52000000, expense: 35000000 },
  { label: "Apr", income: 48000000, expense: 39000000 },
  { label: "Mei", income: 61000000, expense: 42000000 },
  { label: "Jun", income: 58000000, expense: 38000000 },
  { label: "Jul", income: 65000000, expense: 45000000 },
  { label: "Ags", income: 72000000, expense: 51000000 },
  { label: "Sep", income: 68000000, expense: 49000000 },
  { label: "Okt", income: 85000000, expense: 55000000 },
  { label: "Nov", income: 79000000, expense: 52000000 },
  { label: "Des", income: 95000000, expense: 60000000 },
];

export default function RevenueChart() {
  const [periode, setPeriode] = useState("bulan");

  const dataChart = periode === "minggu" ? dataMingguan : dataBulanan;

  return (
    <Paper
      elevation={0}
      sx={{
        p: 3,
        borderRadius: 2,
        border: "1px solid #e5e7eb",
      }}
    >
      {/* Header */}
      <Box
        display="flex"
        justifyContent="space-between"
        alignItems="center"
        flexWrap="wrap"
        gap={2}
        mb={3}
      >
        <Box>
          <Typography variant="h6" fontWeight={700}>
            Grafik Pendapatan
          </Typography>
          <Typography variant="body2" color="text.secondary">
            Perbandingan pendapatan dan pengeluaran
          </Typography>
        </Box>

        {/* Toggle button minggu/bulan */}
        <Stack direction="row" spacing={1}>
          <Button
            onClick={() => setPeriode("minggu")}
            variant={periode === "minggu" ? "contained" : "outlined"}
            size="small"
            sx={{ textTransform: "none", fontWeight: 600 }}
          >
            Mingguan
          </Button>
          <Button
            onClick={() => setPeriode("bulan")}
            variant={periode === "bulan" ? "contained" : "outlined"}
            size="small"
            sx={{ textTransform: "none", fontWeight: 600 }}
          >
            Bulanan
          </Button>
        </Stack>
      </Box>

      {/* Chart */}
      <Box sx={{ height: 350, width: "100%" }}>
        <ResponsiveContainer width="100%" height="100%">
          <LineChart
            data={dataChart}
            margin={{ top: 5, right: 5, left: -20, bottom: 5 }}
          >
            {/* Grid background */}
            <CartesianGrid
              strokeDasharray="3 3"
              vertical={false}
              stroke="#f3f4f6"
            />

            {/* Sumbu X (horizontal) */}
            <XAxis
              dataKey="label"
              axisLine={false}
              tickLine={false}
              tick={{ fill: "#6b7280", fontSize: 12 }}
            />

            {/* Sumbu Y (vertical) */}
            <YAxis
              axisLine={false}
              tickLine={false}
              tick={{ fill: "#6b7280", fontSize: 12 }}
              tickFormatter={formatRupiah}
            />

            {/* Tooltip saat hover */}
            <Tooltip content={<CustomTooltip />} />

            {/* Garis Pendapatan (biru) */}
            <Line
              type="monotone"
              dataKey="income"
              name="Pendapatan"
              stroke="#3b82f6"
              strokeWidth={3}
              dot={false}
              activeDot={{ r: 6, fill: "#3b82f6" }}
            />

            {/* Garis Pengeluaran (merah) */}
            <Line
              type="monotone"
              dataKey="expense"
              name="Pengeluaran"
              stroke="#ef4444"
              strokeWidth={3}
              dot={false}
              activeDot={{ r: 6, fill: "#ef4444" }}
            />
          </LineChart>
        </ResponsiveContainer>
      </Box>

      {/* Legend manual (lebih simple) */}
      <Box display="flex" justifyContent="center" gap={3} mt={2}>
        <Box display="flex" alignItems="center" gap={1}>
          <Box
            sx={{
              width: 12,
              height: 12,
              borderRadius: "50%",
              bgcolor: "#3b82f6",
            }}
          />
          <Typography variant="body2" fontWeight={500}>
            Pendapatan
          </Typography>
        </Box>
        <Box display="flex" alignItems="center" gap={1}>
          <Box
            sx={{
              width: 12,
              height: 12,
              borderRadius: "50%",
              bgcolor: "#ef4444",
            }}
          />
          <Typography variant="body2" fontWeight={500}>
            Pengeluaran
          </Typography>
        </Box>
      </Box>
    </Paper>
  );
}

import { Paper, Typography, Box, Stack, Avatar } from "@mui/material";
import TrendingUpIcon from "@mui/icons-material/TrendingUp";
import TrendingDownIcon from "@mui/icons-material/TrendingDown";

function StatCard({
  title,
  value,
  icon,
  color,
  trend,
  caption,
}: {
  title: string;
  value: string;
  icon: React.ReactNode;
  color: string;
  trend?: { isUp: boolean; percentage: string; label: string } | null;
  caption?: string;
}) {
  return (
    <Paper
      elevation={0}
      sx={{
        p: 3,
        height: "100%",
        display: "flex",
        flexDirection: "column",
        borderRadius: 5,
        bgcolor: "background.paper",
        border: "1px solid",
        borderColor: "divider",
        transition: "0.25s",
        boxShadow:
          "0px 4px 20px rgba(0, 0, 0, 0.04), inset 0 0 0 1px rgba(255,255,255,0.2)",
        "&:hover": {
          boxShadow:
            "0px 8px 30px rgba(0, 0, 0, 0.08), inset 0 0 0 1px rgba(255,255,255,0.3)",
          transform: "translateY(-2px)",
        },
      }}
    >
      <Stack
        direction="row"
        alignItems="flex-start"
        justifyContent="space-between"
        spacing={2}
      >
        <Box>
          <Typography
            variant="subtitle2"
            color="text.secondary"
            fontWeight={600}
            letterSpacing={0.3}
            gutterBottom
          >
            {title}
          </Typography>

          <Typography variant="h4" fontWeight={700} sx={{ lineHeight: 1.2 }}>
            {value}
          </Typography>

          {caption && (
            <Typography variant="body1" color="text.secondary" sx={{ mt: 0.5 }}>
              {caption}
            </Typography>
          )}
        </Box>

        <Avatar
          variant="rounded"
          sx={{
            backdropFilter: "blur(12px)",
            bgcolor: `rgba(0, 123, 255, 0.08)`,
            color: `${color}.main`,
            width: 52,
            height: 52,
            borderRadius: 3,
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            boxShadow:
              "0 4px 12px rgba(0,0,0,0.06), inset 0 0 0 1px rgba(255,255,255,0.3)",
          }}
        >
          {icon}
        </Avatar>
      </Stack>

      {trend && (
        <Stack direction="row" alignItems="center" spacing={1} sx={{ mt: 2 }}>
          <Stack
            direction="row"
            alignItems="center"
            sx={{
              px: 1,
              py: 0.3,
              borderRadius: 2,
              fontWeight: 600,
              fontSize: "0.75rem",
              color: trend.isUp ? "success.main" : "error.main",
              bgcolor: trend.isUp
                ? "rgba(34,197,94,0.12)"
                : "rgba(239,68,68,0.12)",
            }}
          >
            {trend.isUp ? (
              <TrendingUpIcon fontSize="small" />
            ) : (
              <TrendingDownIcon fontSize="small" />
            )}
            <Typography variant="body2" fontWeight={700} sx={{ ml: 0.5 }}>
              {trend.percentage}
            </Typography>
          </Stack>

          <Typography variant="caption" color="text.secondary">
            {trend.label}
          </Typography>
        </Stack>
      )}
    </Paper>
  );
}

export default StatCard;

