import { Grid, Paper, Typography, Box } from "@mui/material";
// Import Ikon-ikon yang diperlukan
import AttachMoneyIcon from "@mui/icons-material/AttachMoney";
import GroupIcon from "@mui/icons-material/Group";
import LocalLaundryServiceIcon from "@mui/icons-material/LocalLaundryService";
import CheckCircleIcon from "@mui/icons-material/CheckCircle";
import MeetingRoomIcon from "@mui/icons-material/MeetingRoom";
import StatCard from "./components/StatCard";
import AddDataCard from "./components/AddDataCard";
import RevenueChart from "./components/RevenueChart";
import RecentTransactionsCard from "./components/DashboardCards/RecentTransactionsCard";
import OccupancyRateCard from "./components/DashboardCards/OccupancyRateCard";
import UpcomingRentCard from "./components/DashboardCards/UpcomingRentCard";
import DashboardContent from "./components/DashboardCards/DashboardContent";
import { createDashboardMetadata } from "@/utils/metadata";

export const metadata = createDashboardMetadata({
  subtitle: "Analytics",
  description: "Analytics Dashboard",
});

const transactionsData = [
  {
    id: "1",
    name: "Budi Santoso",
    description: "Sewa bulan Juni",
    amount: "Rp 1.500.000",
    date: "15 Jun 2023",
    status: "paid" as const,
  },
  {
    id: "2",
    name: "Siti Aminah",
    description: "Sewa bulan Juni",
    amount: "Rp 1.200.000",
    date: "16 Jun 2023",
    status: "pending" as const,
  },
  {
    id: "3",
    name: "Ahmad Fauzi",
    description: "Sewa bulan Juni",
    amount: "Rp 1.800.000",
    date: "10 Jun 2023",
    status: "overdue" as const,
  },
  {
    id: "4",
    name: "Rina Kartika",
    description: "Sewa bulan Juni",
    amount: "Rp 1.400.000",
    date: "12 Jun 2023",
    status: "paid" as const,
  },
  {
    id: "5",
    name: "Dedi Prasetyo",
    description: "Sewa bulan Juni",
    amount: "Rp 1.600.000",
    date: "18 Jun 2023",
    status: "pending" as const,
  },
];

const rentData = [
  {
    id: "1",
    residentName: "Budi Santoso",
    unit: "A-101",
    amount: "Rp 1.500.000",
    dueDate: "5 Jul 2023",
    daysLeft: 3,
  },
  {
    id: "2",
    residentName: "Siti Aminah",
    unit: "B-202",
    amount: "Rp 1.200.000",
    dueDate: "7 Jul 2023",
    daysLeft: 5,
  },
  {
    id: "3",
    residentName: "Ahmad Fauzi",
    unit: "C-301",
    amount: "Rp 1.800.000",
    dueDate: "Today",
    daysLeft: 0,
  },
  {
    id: "4",
    residentName: "Rina Kartika",
    unit: "A-103",
    amount: "Rp 1.400.000",
    dueDate: "3 Jul 2023",
    daysLeft: 1,
  },
  {
    id: "5",
    residentName: "Dedi Prasetyo",
    unit: "D-401",
    amount: "Rp 1.600.000",
    dueDate: "8 Jul 2023",
    daysLeft: 6,
  },
];

// --- 3. HALAMAN UTAMA ---
export default function DashboardPage() {
  // Mock occupancy data
  const occupancyData = {
    occupiedUnits: 42,
    totalUnits: 50,
    occupancyPercentage: 84,
  };

  // Memperbarui statsData dengan ikon yang benar
  const statsDataWithIcons = [
    {
      id: 1,
      title: "Total Pendapatan",
      value: "Rp 15.2M",
      icon: <AttachMoneyIcon />,
      color: "primary",
      trend: { isUp: true, percentage: "+12%", label: "dari bulan lalu" },
    },
    {
      id: 2,
      title: "Total Pengguna",
      value: "1,250",
      caption: "users",
      icon: <GroupIcon />,
      color: "info",
      trend: { isUp: true, percentage: "+5%", label: "minggu ini" },
    },
    {
      id: 3,
      title: "Laundry Pending",
      value: "34",
      caption: "orders",
      icon: <LocalLaundryServiceIcon />,
      color: "warning",
      trend: {
        isUp: false,
        percentage: "-2%",
        label: "lebih sedikit dari kemarin",
      },
    },
    {
      id: 4,
      title: "Unit Kost Kosong",
      value: "3",
      icon: <MeetingRoomIcon />,
      color: "error",
      trend: {
        isUp: true,
        percentage: "+3",
        label: "Kamar tersedia",
      },
    },
  ];

  return (
    <Box sx={{ p: 2 }}>
      <DashboardContent
        statsData={statsDataWithIcons}
        occupancyData={occupancyData}
        transactionsData={transactionsData}
        rentData={rentData}
      />
    </Box>
  );
}

