content.text:
'use client';

import React from 'react';
import {
  Card,
  CardHeader,
  CardContent,
  Grid,
  Button,
  Typography,
  Box,
  Avatar,
} from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import PeopleIcon from '@mui/icons-material/People';
import ReceiptIcon from '@mui/icons-material/Receipt';
import MeetingRoomIcon from '@mui/icons-material/MeetingRoom';
import LocalLaundryServiceIcon from '@mui/icons-material/LocalLaundryService';

interface ActionItem {
  id: string;
  title: string;
  icon: React.ReactNode;
  onClick: () => void;
  color?: string;
}

interface QuickActionsCardProps {
  title?: string;
  actions: ActionItem[];
}

const QuickActionsCard: React.FC<QuickActionsCardProps> = ({
  title = 'Aksi Cepat',
  actions,
}) => {
  return (
    <Card
      sx={{
        height: '100%',
        display: 'flex',
        flexDirection: 'column',
        background: 'linear-gradient(135deg, #f5f7fa 0%, #e4edf9 100%)',
        boxShadow: '0 4px 12px rgba(0,0,0,0.05)'
      }}
    >
      <CardHeader
        avatar={
          <Avatar sx={{ bgcolor: 'primary.main' }}>
            <AddIcon />
          </Avatar>
        }
        title={
          <Typography variant="h6" fontWeight={600} color="text.primary">
            {title}
          </Typography>
        }
        sx={{ pb: 1 }}
      />
      <CardContent sx={{ flex: 1, py: 2 }}>
        <Grid container spacing={2}>
          {actions.map((action) => (
            <Grid key={action.id} item xs={6}>
              <Button
                variant="outlined"
                fullWidth
                onClick={action.onClick}
                startIcon={action.icon}
                sx={{
                  minHeight: '80px',
                  display: 'flex',
                  flexDirection: 'column',
                  justifyContent: 'center',
                  alignItems: 'center',
                  py: 2,
                }}
              >
                <Typography variant="button" component="span">
                  {action.title}
                </Typography>
              </Button>
            </Grid>
          ))}
        </Grid>
      </CardContent>
    </Card>
  );
};

// Export default dengan action items bawaan untuk kemudahan
const DefaultQuickActionsCard: React.FC<{ onAddResident?: () => void; onRecordPayment?: () => void; onAddUnit?: () => void; onManageLaundry?: () => void }> = ({
  onAddResident = () => {},
  onRecordPayment = () => {},
  onAddUnit = () => {},
  onManageLaundry = () => {},
}) => {
  const defaultActions = [
    {
      id: 'add-resident',
      title: 'Tambah Penghuni',
      icon: <PeopleIcon />,
      onClick: onAddResident,
      color: '#4caf50',
    },
    {
      id: 'record-payment',
      title: 'Catat Pembayaran',
      icon: <ReceiptIcon />,
      onClick: onRecordPayment,
      color: '#2196f3',
    },
    {
      id: 'add-unit',
      title: 'Tambah Unit',
      icon: <MeetingRoomIcon />,
      onClick: onAddUnit,
      color: '#ff9800',
    },
    {
      id: 'manage-laundry',
      title: 'Layanan Laundry',
      icon: <LocalLaundryServiceIcon />,
      onClick: onManageLaundry,
      color: '#9c27b0',
    },
  ];

  return <QuickActionsCard actions={defaultActions} />;
};

export default DefaultQuickActionsCard;

