'use client';

import React from 'react';
import {
  Card,
  CardHeader,
  CardContent,
  Grid,
  Button,
  Typography,
  Box,
  Avatar,
} from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import PeopleIcon from '@mui/icons-material/People';
import ReceiptIcon from '@mui/icons-material/Receipt';
import MeetingRoomIcon from '@mui/icons-material/MeetingRoom';
import LocalLaundryServiceIcon from '@mui/icons-material/LocalLaundryService';

interface ActionItem {
  id: string;
  title: string;
  icon: React.ReactNode;
  onClick: () => void;
  color?: string;
}

interface QuickActionsCardProps {
  title?: string;
  actions: ActionItem[];
}

const QuickActionsCard: React.FC<QuickActionsCardProps> = ({
  title = 'Aksi Cepat',
  actions,
}) => {
  return (
    <Card
      sx={{
        height: '100%',
        display: 'flex',
        flexDirection: 'column',
        bgcolor: 'white',
        borderRadius: 2,
        boxShadow: '0 4px 12px rgba(0,0,0,0.05)',
        border: '1px solid',
        borderColor: 'divider'
      }}
    >
      <CardHeader
        avatar={
          <Avatar sx={{
            bgcolor: 'primary.main',
            color: 'white'
          }}>
            <AddIcon />
          </Avatar>
        }
        title={
          <Typography variant="h6" fontWeight={600} color="text.primary">
            {title}
          </Typography>
        }
        sx={{ pb: 1 }}
      />
      <CardContent sx={{ flex: 1, py: 2 }}>
        <Grid container spacing={1.5}>
          {actions.map((action) => (
            <Grid key={action.id} item xs={6}>
              <Button
                variant="text"
                fullWidth
                onClick={action.onClick}
                sx={{
                  minHeight: '90px',
                  display: 'flex',
                  flexDirection: 'column',
                  justifyContent: 'center',
                  alignItems: 'center',
                  py: 1.5,
                  px: 1,
                  borderRadius: 1.5,
                  transition: 'all 0.3s ease',
                  color: 'text.primary',
                  '&:hover': {
                    transform: 'translateY(-3px)',
                    boxShadow: '0 6px 16px rgba(0,0,0,0.1)',
                    bgcolor: 'rgba(0, 0, 0, 0.02)',
                  },
                  border: '1px solid',
                  borderColor: 'divider'
                }}
              >
                <Avatar
                  sx={{
                    width: 40,
                    height: 40,
                    mb: 1,
                    bgcolor: action.color || 'primary.light',
                    color: 'white'
                  }}
                >
                  {action.icon}
                </Avatar>
                <Typography
                  variant="caption"
                  component="span"
                  fontWeight={600}
                  color="text.primary"
                >
                  {action.title}
                </Typography>
              </Button>
            </Grid>
          ))}
        </Grid>
      </CardContent>
    </Card>
  );
};

// Export default dengan action items bawaan untuk kemudahan
const DefaultQuickActionsCard: React.FC<{ onAddResident?: () => void; onRecordPayment?: () => void; onAddUnit?: () => void; onManageLaundry?: () => void }> = ({
  onAddResident = () => {},
  onRecordPayment = () => {},
  onAddUnit = () => {},
  onManageLaundry = () => {},
}) => {
  const defaultActions = [
    {
      id: 'add-resident',
      title: 'Tambah Penghuni',
      icon: <PeopleIcon />,
      onClick: onAddResident,
      color: '#4caf50',
    },
    {
      id: 'record-payment',
      title: 'Catat Pembayaran',
      icon: <ReceiptIcon />,
      onClick: onRecordPayment,
      color: '#2196f3',
    },
    {
      id: 'add-unit',
      title: 'Tambah Unit',
      icon: <MeetingRoomIcon />,
      onClick: onAddUnit,
      color: '#ff9800',
    },
    {
      id: 'manage-laundry',
      title: 'Layanan Laundry',
      icon: <LocalLaundryServiceIcon />,
      onClick: onManageLaundry,
      color: '#9c27b0',
    },
  ];

  return <QuickActionsCard actions={defaultActions} />;
};

export default DefaultQuickActionsCard;

import React from 'react';
import {
  Card,
  CardHeader,
  CardContent,
  Typography,
  List,
  ListItem,
  ListItemIcon,
  Box,
  Chip,
  Divider,
  Avatar,
} from '@mui/material';
import ReceiptIcon from '@mui/icons-material/Receipt';
import AttachMoneyIcon from '@mui/icons-material/AttachMoney';

interface Transaction {
  id: string;
  name: string;
  description: string;
  amount: string;
  date: string;
  status: 'paid' | 'pending' | 'overdue';
}

interface RecentTransactionsCardProps {
  title?: string;
  transactions: Transaction[];
}

const getStatusColor = (status: string) => {
  switch (status) {
    case 'paid':
      return '#4caf50';
    case 'pending':
      return '#ff9800';
    case 'overdue':
      return '#f44336';
    default:
      return '#9e9e9e';
  }
};

const getStatusBgColor = (status: string) => {
  switch (status) {
    case 'paid':
      return '#e8f5e9';
    case 'pending':
      return '#fff3e0';
    case 'overdue':
      return '#ffebee';
    default:
      return '#f5f5f5';
  }
};

const getStatusLabel = (status: string) => {
  switch (status) {
    case 'paid':
      return 'Lunas';
    case 'pending':
      return 'Pending';
    case 'overdue':
      return 'Terlambat';
    default:
      return status;
  }
};

const RecentTransactionsCard: React.FC<RecentTransactionsCardProps> = ({
  title = 'Transaksi Terbaru',
  transactions,
}) => {
  return (
    <Card
      sx={{
        height: '100%',
        display: 'flex',
        flexDirection: 'column',
        background: 'linear-gradient(135deg, #f5f7fa 0%, #e4edf9 100%)',
        boxShadow: '0 4px 12px rgba(0,0,0,0.05)'
      }}
    >
      <CardHeader
        avatar={<ReceiptIcon color="primary" />}
        title={title}
        sx={{ pb: 1 }}
      />
      <CardContent sx={{ flex: 1, pb: 1 }}>
        <List dense>
          {transactions.slice(0, 5).map((transaction) => (
            <React.Fragment key={transaction.id}>
              <ListItem alignItems="flex-start" sx={{ py: 1 }}>
                <ListItemIcon sx={{ minWidth: '32px', color: getStatusColor(transaction.status) }}>
                  <AttachMoneyIcon fontSize="small" />
                </ListItemIcon>
                <Box flex={1}>
                  <Typography variant="body2" fontWeight={500}>
                    {transaction.name}
                  </Typography>
                  <Typography variant="caption" color="text.secondary" display="block" gutterBottom={0.5}>
                    {transaction.description}
                  </Typography>
                  <Box component="span" display="flex" justifyContent="space-between" alignItems="center" mt={0.5}>
                    <Typography variant="subtitle2" color="primary" component="span">
                      {transaction.amount}
                    </Typography>
                    <Chip
                      label={getStatusLabel(transaction.status)}
                      size="small"
                      variant="outlined"
                      sx={{
                        fontSize: '0.7rem',
                        height: '20px',
                        color: getStatusColor(transaction.status),
                        borderColor: getStatusColor(transaction.status),
                      }}
                    />
                  </Box>
                </Box>
              </ListItem>
              <Divider variant="middle" component="li" />
            </React.Fragment>
          ))}
        </List>
      </CardContent>
    </Card>
  );
};

export default RecentTransactionsCard;

import React from 'react';
import {
  Card,
  CardHeader,
  CardContent,
  Typography,
  List,
  ListItem,
  ListItemIcon,
  Box,
  Chip,
  Divider,
  Avatar,
} from '@mui/material';
import { orange, green } from '@mui/material/colors';
import CalendarTodayIcon from '@mui/icons-material/CalendarToday';
import EventNoteIcon from '@mui/icons-material/EventNote';

interface RentPayment {
  id: string;
  residentName: string;
  unit: string;
  amount: string;
  dueDate: string;
  daysLeft: number;
}

interface UpcomingRentCardProps {
  title?: string;
  rentPayments: RentPayment[];
}

const getDaysLeftColor = (daysLeft: number) => {
  if (daysLeft <= 0) return '#f44336'; // merah untuk hari ini atau terlambat
  if (daysLeft <= 3) return '#ff9800'; // oranye untuk mendekati jatuh tempo
  return '#4caf50'; // hijau untuk masih jauh
};

const getDaysLeftBgColor = (daysLeft: number) => {
  if (daysLeft <= 0) return '#ffebee'; // latar merah muda
  if (daysLeft <= 3) return '#fff3e0'; // latar oranye muda
  return '#e8f5e9'; // latar hijau muda
};

const UpcomingRentCard: React.FC<UpcomingRentCardProps> = ({
  title = 'Sewa Mendatang',
  rentPayments,
}) => {
  return (
    <Card
      sx={{
        height: '100%',
        display: 'flex',
        flexDirection: 'column',
        background: 'linear-gradient(135deg, #f5f7fa 0%, #e4edf9 100%)',
        boxShadow: '0 4px 12px rgba(0,0,0,0.05)'
      }}
    >
      <CardHeader
        avatar={<CalendarTodayIcon color="primary" />}
        title={title}
        sx={{ pb: 1 }}
      />
      <CardContent sx={{ flex: 1, pb: 1 }}>
        <List dense>
          {rentPayments.slice(0, 5).map((payment, index) => (
            <React.Fragment key={payment.id}>
              <ListItem alignItems="flex-start" sx={{ py: 1 }}>
                <ListItemIcon sx={{ minWidth: '32px', color: payment.daysLeft <= 3 ? orange[500] : green[500] }}>
                  <EventNoteIcon fontSize="small" />
                </ListItemIcon>
                <Box flex={1}>
                  <Typography variant="body2" fontWeight={500}>
                    {payment.residentName}
                  </Typography>
                  <Typography variant="caption" color="text.secondary" display="block" gutterBottom={0.5}>
                    Unit: {payment.unit} • Jatuh tempo: {payment.dueDate}
                  </Typography>
                  <Box component="span" display="flex" justifyContent="space-between" alignItems="center" mt={0.5}>
                    <Typography variant="subtitle2" color="primary" component="span">
                      {payment.amount}
                    </Typography>
                    <Chip
                      label={payment.daysLeft <= 0 ? 'Hari Ini' : `${payment.daysLeft} Hari Lagi`}
                      size="small"
                      variant="outlined"
                      sx={{
                        fontSize: '0.7rem',
                        height: '20px',
                        color: payment.daysLeft <= 3 ? orange[500] : green[500],
                        borderColor: payment.daysLeft <= 3 ? orange[500] : green[500],
                      }}
                    />
                  </Box>
                </Box>
              </ListItem>
              <Divider variant="middle" component="li" />
            </React.Fragment>
          ))}
        </List>
      </CardContent>
    </Card>
  );
};

export default UpcomingRentCard;

