      <CardContent sx={{ flex: 1, pb: 1, pt: 0.5 }}>
        <List dense sx={{ py: 0.5 }}>
          {rentPayments.slice(0, 5).map((payment) => (
            <React.Fragment key={payment.id}>
              <ListItem 
                alignItems="flex-start" 
                sx={{ 
                  py: 0.75,
                  borderRadius: 1.5,
                  mb: 0.5,
                  '&:hover': { bgcolor: 'action.hover' },
                  bgcolor: 'background.paper',
                  border: '1px solid',
                  borderColor: 'divider'
                }}
              >
                <ListItemIcon 
                  sx={{ 
                    minWidth: '30px', 
                    color: payment.daysLeft <= 3 ? orange[500] : green[500],
                    alignItems: 'center'
                  }}
                >
                  <EventNoteIcon fontSize="small" />
                </ListItemIcon>
                <Box flex={1}>
                  <Typography variant="body2" fontWeight={500} fontSize="0.875rem">
                    {payment.residentName}
                  </Typography>
                  <Typography variant="caption" color="text.secondary" display="block" mb={0.25}>
                    Unit: {payment.unit} • Jatuh tempo: {payment.dueDate}
                  </Typography>
                  <Box component="span" display="flex" justifyContent="space-between" alignItems="center" mt={0.25}>
                    <Typography variant="body2" color="primary.main" fontWeight={600} fontSize="0.875rem">
                      {payment.amount}
                    </Typography>
                    <Chip
                      label={payment.daysLeft <= 0 ? 'Hari Ini' : `${payment.daysLeft} Hari Lagi`}
                      size="small"
                      variant="outlined"
                      sx={{
                        fontSize: '0.65rem',
                        height: '18px',
                        color: payment.daysLeft <= 3 ? orange[500] : green[500],
                        borderColor: payment.daysLeft <= 3 ? orange[500] : green[500],
                        bgcolor: payment.daysLeft <= 3 ? '#fff3e0' : '#e8f5e9',
                        borderRadius: 1
                      }}
                    />
                  </Box>
                </Box>
              </ListItem>
            </React.Fragment>
          ))}
        </List>
      </CardContent>
    </Card>
  );
};"}

