import React from "react";
import PeopleIcon from "@mui/icons-material/People";
import ReceiptIcon from "@mui/icons-material/Receipt";
import MeetingRoomIcon from "@mui/icons-material/MeetingRoom";
import LocalLaundryServiceIcon from "@mui/icons-material/LocalLaundryService";

interface ActionItem {
  id: string;
  title: string;
  icon: React.ReactNode;
  onClick: () => void;
  color?: string;
}

interface QuickActionsCardProps {
  title?: string;
  actions?: ActionItem[];
}

const QuickActionsCard: React.FC<QuickActionsCardProps> = ({
  title = "Aksi Cepat",
  actions = [],
}) => {
  // Default actions jika tidak ada yang diberikan
  const defaultActions: ActionItem[] = [
    {
      id: "add-resident",
      title: "Tambah Penghuni",
      icon: <PeopleIcon sx={{ fontSize: 20 }} />,
      onClick: () => console.log("Tambah Penghuni"),
      color: "text-green-600",
    },
    {
      id: "record-payment",
      title: "Catat Pembayaran",
      icon: <ReceiptIcon sx={{ fontSize: 20 }} />,
      onClick: () => console.log("Catat Pembayaran"),
      color: "text-blue-600",
    },
    {
      id: "add-unit",
      title: "Tambah Unit",
      icon: <MeetingRoomIcon sx={{ fontSize: 20 }} />,
      onClick: () => console.log("Tambah Unit"),
      color: "text-orange-600",
    },
    {
      id: "manage-laundry",
      title: "Layanan Laundry",
      icon: <LocalLaundryServiceIcon sx={{ fontSize: 20 }} />,
      onClick: () => console.log("Layanan Laundry"),
      color: "text-purple-600",
    },
  ];

  const displayActions = actions.length > 0 ? actions : defaultActions;

  return (
    <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-4">
      {/* Header */}
      <h3 className="font-semibold text-gray-800 text-sm mb-3">{title}</h3>

      {/* Actions Grid */}
      <div className="grid grid-cols-2 gap-2">
        {displayActions.map((action) => (
          <button
            key={action.id}
            onClick={action.onClick}
            className="flex flex-col items-center justify-center gap-1.5 p-3 rounded-lg bg-gray-50 hover:bg-gray-100 transition-colors"
          >
            <div className={action.color || "text-gray-600"}>{action.icon}</div>
            <span className="text-xs font-medium text-gray-600 text-center leading-tight">
              {action.title}
            </span>
          </button>
        ))}
      </div>
    </div>
  );
};

// Demo
export default function App() {
  return (
    <div className="min-h-screen bg-gray-50 p-6">
      <div className="max-w-sm mx-auto space-y-4">
        {/* Default actions */}
        <QuickActionsCard />

        {/* Custom actions example */}
        <QuickActionsCard
          title="Custom Actions"
          actions={[
            {
              id: "action1",
              title: "Action 1",
              icon: <PeopleIcon sx={{ fontSize: 20 }} />,
              onClick: () => alert("Action 1"),
              color: "text-indigo-600",
            },
            {
              id: "action2",
              title: "Action 2",
              icon: <ReceiptIcon sx={{ fontSize: 20 }} />,
              onClick: () => alert("Action 2"),
              color: "text-pink-600",
            },
          ]}
        />
      </div>
    </div>
  );
}

