import React from "react";
import {
  Card,
  CardHeader,
  CardContent,
  Typography,
  Box,
  Stack,
  Avatar,
} from "@mui/material";
import MeetingRoomIcon from "@mui/icons-material/MeetingRoom";

interface OccupancyRateCardProps {
  title?: string;
  occupiedUnits: number;
  totalUnits: number;
  occupancyPercentage: number;
}

const OccupancyRateCard: React.FC<OccupancyRateCardProps> = ({
  title = "Tingkat Okupansi",
  occupiedUnits,
  totalUnits,
  occupancyPercentage,
}) => {
  const availableUnits = totalUnits - occupiedUnits;

  // Menentukan warna berdasarkan tingkat okupansi
  const getOccupancyColor = () => {
    if (occupancyPercentage >= 90) return "#4caf50"; // hijau
    if (occupancyPercentage >= 70) return "#ff9800"; // oranye
    return "#f44336"; // merah
  };

  // Membuat ring progress dengan SVG
  const size = 120;
  const strokeWidth = 8;
  const radius = (size - strokeWidth) / 2;
  const circumference = radius * 2 * Math.PI;
  const offset = circumference - (occupancyPercentage / 100) * circumference;

  return (
    <Card
      sx={{
        height: "100%",
        display: "flex",
        flexDirection: "column",
        background: "linear-gradient(135deg, #f5f7fa 0%, #e4edf9 100%)",
        boxShadow: "0 4px 12px rgba(0,0,0,0.05)"
      }}
    >
      <CardHeader
        avatar={
          <Avatar
            sx={{
              bgcolor: getOccupancyColor()
            }}
          >
            <MeetingRoomIcon />
          </Avatar>
        }
        title={
          <Typography variant="h6" fontWeight={600} color="text.primary">
            {title}
          </Typography>
        }
        sx={{ pb: 1 }}
      />
      <CardContent sx={{ flex: 1, display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center' }}>
        <Box sx={{ position: 'relative', display: 'inline-flex', alignItems: 'center', justifyContent: 'center' }}>
          <svg width={size} height={size} viewBox={`0 0 ${size} ${size}`}>
            {/* Background ring */}
            <circle
              cx={size / 2}
              cy={size / 2}
              r={radius}
              fill="none"
              stroke="#e0e0e0"
              strokeWidth={strokeWidth}
            />
            {/* Progress ring */}
            <circle
              cx={size / 2}
              cy={size / 2}
              r={radius}
              fill="none"
              stroke={getOccupancyColor()}
              strokeWidth={strokeWidth}
              strokeLinecap="round"
              strokeDasharray={circumference}
              strokeDashoffset={offset}
              transform={`rotate(-90 ${size / 2} ${size / 2})`}
              style={{
                transition: 'stroke-dashoffset 1s ease-in-out',
                transitionTimingFunction: 'cubic-bezier(0.34, 1.56, 0.64, 1)',
              }}
            />
          </svg>
          <Box
            sx={{
              top: 0,
              left: 0,
              bottom: 0,
              right: 0,
              position: 'absolute',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
            }}
          >
            <Typography variant="h4" component="div" fontWeight={700} color={getOccupancyColor()}>
              {occupancyPercentage}%
            </Typography>
          </Box>
        </Box>

        <Stack direction="row" spacing={3} mt={2} width="100%">
          <Box textAlign="center" flex={1}>
            <Typography variant="h6" fontWeight={600} color="primary.main">
              {occupiedUnits}
            </Typography>
            <Typography variant="caption" color="text.secondary">
              Terisi
            </Typography>
          </Box>
          <Box textAlign="center" flex={1}>
            <Typography variant="h6" fontWeight={600} color="error.main">
              {availableUnits}
            </Typography>
            <Typography variant="caption" color="text.secondary">
              Kosong
            </Typography>
          </Box>
          <Box textAlign="center" flex={1}>
            <Typography variant="h6" fontWeight={600} color="text.primary">
              {totalUnits}
            </Typography>
            <Typography variant="caption" color="text.secondary">
              Total
            </Typography>
          </Box>
        </Stack>

        <Box mt={2} width="100%">
          <Typography
            variant="caption"
            align="center"
            color={getOccupancyColor()}
            fontWeight={600}
            display="block"
          >
            {occupancyPercentage >= 90
              ? "Okupansi sangat tinggi!"
              : occupancyPercentage >= 70
                ? "Okupansi bagus"
                : "Perlu tingkatkan okupansi"}
          </Typography>
        </Box>
      </CardContent>
    </Card>
  );
};

export default OccupancyRateCard;

'use client';

import React from 'react';
import {
  Card,
  CardHeader,
  CardContent,
  Grid,
  Button,
  Typography,
  Box,
  Avatar,
} from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import PeopleIcon from '@mui/icons-material/People';
import ReceiptIcon from '@mui/icons-material/Receipt';
import MeetingRoomIcon from '@mui/icons-material/MeetingRoom';
import LocalLaundryServiceIcon from '@mui/icons-material/LocalLaundryService';

interface ActionItem {
  id: string;
  title: string;
  icon: React.ReactNode;
  onClick: () => void;
  color?: string;
}

interface QuickActionsCardProps {
  title?: string;
  actions: ActionItem[];
}

const QuickActionsCard: React.FC<QuickActionsCardProps> = ({
  title = 'Aksi Cepat',
  actions,
}) => {
  return (
    <Card
      sx={{
        height: '100%',
        display: 'flex',
        flexDirection: 'column',
        bgcolor: 'white',
        borderRadius: 2,
        boxShadow: '0 2px 8px rgba(0,0,0,0.05)',
        border: '1px solid',
        borderColor: 'divider'
      }}
    >
      <CardHeader
        avatar={
          <Avatar sx={{
            bgcolor: 'primary.main',
            color: 'white',
            width: 32,
            height: 32
          }}>
            <AddIcon fontSize="small" />
          </Avatar>
        }
        title={
          <Typography variant="subtitle1" fontWeight={600} color="text.primary">
            {title}
          </Typography>
        }
        sx={{ pb: 0.5, pt: 1 }}
      />
      <CardContent sx={{ flex: 1, py: 1, px: 1.5 }}>
        <Grid container spacing={0.75}>
          {actions.map((action) => (
            <Grid key={action.id} item xs={6}>
              <Button
                variant="text"
                fullWidth
                onClick={action.onClick}
                sx={{
                  minHeight: '60px',
                  display: 'flex',
                  flexDirection: 'column',
                  justifyContent: 'center',
                  alignItems: 'center',
                  py: 0.75,
                  px: 0.5,
                  borderRadius: 1,
                  transition: 'all 0.2s ease',
                  color: 'text.primary',
                  '&:hover': {
                    transform: 'translateY(-1px)',
                    boxShadow: '0 2px 8px rgba(0,0,0,0.08)',
                    bgcolor: 'rgba(0, 0, 0, 0.02)',
                  },
                  border: '1px solid',
                  borderColor: 'divider'
                }}
              >
                <Avatar
                  sx={{
                    width: 28,
                    height: 28,
                    mb: 0.5,
                    bgcolor: action.color || 'primary.light',
                    color: 'white'
                  }}
                >
                  {action.icon}
                </Avatar>
                <Typography
                  variant="caption"
                  component="span"
                  fontWeight={500}
                  color="text.primary"
                  sx={{ fontSize: '0.65rem' }}
                >
                  {action.title}
                </Typography>
              </Button>
            </Grid>
          ))}
        </Grid>
      </CardContent>
    </Card>
  );
};

// Export default dengan action items bawaan untuk kemudahan
const DefaultQuickActionsCard: React.FC<{ onAddResident?: () => void; onRecordPayment?: () => void; onAddUnit?: () => void; onManageLaundry?: () => void }> = ({
  onAddResident = () => {},
  onRecordPayment = () => {},
  onAddUnit = () => {},
  onManageLaundry = () => {},
}) => {
  const defaultActions = [
    {
      id: 'add-resident',
      title: 'Tambah Penghuni',
      icon: <PeopleIcon />,
      onClick: onAddResident,
      color: '#4caf50',
    },
    {
      id: 'record-payment',
      title: 'Catat Pembayaran',
      icon: <ReceiptIcon />,
      onClick: onRecordPayment,
      color: '#2196f3',
    },
    {
      id: 'add-unit',
      title: 'Tambah Unit',
      icon: <MeetingRoomIcon />,
      onClick: onAddUnit,
      color: '#ff9800',
    },
    {
      id: 'manage-laundry',
      title: 'Layanan Laundry',
      icon: <LocalLaundryServiceIcon />,
      onClick: onManageLaundry,
      color: '#9c27b0',
    },
  ];

  return <QuickActionsCard actions={defaultActions} />;
};

export default DefaultQuickActionsCard;

