"use client";

import React from "react";
import {
  Card,
  CardHeader,
  CardContent,
  Button,
  Typography,
  Avatar,
} from "@mui/material";

import Grid from "@mui/material/Grid";

import AddIcon from "@mui/icons-material/Add";
import PeopleIcon from "@mui/icons-material/People";
import ReceiptIcon from "@mui/icons-material/Receipt";
import MeetingRoomIcon from "@mui/icons-material/MeetingRoom";
import LocalLaundryServiceIcon from "@mui/icons-material/LocalLaundryService";

interface ActionItem {
  id: string;
  title: string;
  icon: React.ReactNode;
  onClick: () => void;
  color?: string;
}

interface QuickActionsCardProps {
  title?: string;
  actions: ActionItem[];
}

const QuickActionsCard: React.FC<QuickActionsCardProps> = ({
  title = "Aksi Cepat",
  actions,
}) => {
  return (
    <Card
      sx={{
        height: "100%",
        display: "flex",
        flexDirection: "column",
        bgcolor: "white",
        borderRadius: 2,
        boxShadow: "0 2px 8px rgba(0,0,0,0.05)",
        border: "1px solid",
        borderColor: "divider",
      }}
    >
      <CardHeader
        avatar={
          <Avatar
            sx={{
              bgcolor: "primary.main",
              color: "white",
              width: 32,
              height: 32,
            }}
          >
            <AddIcon fontSize="small" />
          </Avatar>
        }
        title={
          <Typography variant="subtitle1" fontWeight={600} color="text.primary">
            {title}
          </Typography>
        }
        sx={{ pb: 0.5, pt: 1 }}
      />

      <CardContent sx={{ flex: 1, py: 1, px: 1.5 }}>
        <Grid container spacing={0.75}>
          {actions.map((action) => (
            <Grid key={action.id} xs={6}>
              <Button
                variant="text"
                fullWidth
                onClick={action.onClick}
                sx={{
                  minHeight: "60px",
                  display: "flex",
                  flexDirection: "column",
                  justifyContent: "center",
                  alignItems: "center",
                  py: 0.75,
                  px: 0.5,
                  borderRadius: 1,
                  transition: "all 0.2s ease",
                  color: "text.primary",
                  "&:hover": {
                    transform: "translateY(-1px)",
                    boxShadow: "0 2px 8px rgba(0,0,0,0.08)",
                    bgcolor: "rgba(0, 0, 0, 0.02)",
                  },
                  border: "1px solid",
                  borderColor: "divider",
                }}
              >
                <Avatar
                  sx={{
                    width: 28,
                    height: 28,
                    mb: 0.5,
                    bgcolor: action.color || "primary.light",
                    color: "white",
                  }}
                >
                  {action.icon}
                </Avatar>

                <Typography
                  variant="caption"
                  fontWeight={500}
                  sx={{ fontSize: "0.65rem" }}
                >
                  {action.title}
                </Typography>
              </Button>
            </Grid>
          ))}
        </Grid>
      </CardContent>
    </Card>
  );
};

// Default Actions
const DefaultQuickActionsCard: React.FC<{
  onAddResident?: () => void;
  onRecordPayment?: () => void;
  onAddUnit?: () => void;
  onManageLaundry?: () => void;
}> = ({
  onAddResident = () => {},
  onRecordPayment = () => {},
  onAddUnit = () => {},
  onManageLaundry = () => {},
}) => {
  const defaultActions = [
    {
      id: "add-resident",
      title: "Tambah Penghuni",
      icon: <PeopleIcon />,
      onClick: onAddResident,
      color: "#4caf50",
    },
    {
      id: "record-payment",
      title: "Catat Pembayaran",
      icon: <ReceiptIcon />,
      onClick: onRecordPayment,
      color: "#2196f3",
    },
    {
      id: "add-unit",
      title: "Tambah Unit",
      icon: <MeetingRoomIcon />,
      onClick: onAddUnit,
      color: "#ff9800",
    },
    {
      id: "manage-laundry",
      title: "Layanan Laundry",
      icon: <LocalLaundryServiceIcon />,
      onClick: onManageLaundry,
      color: "#9c27b0",
    },
  ];

  return <QuickActionsCard actions={defaultActions} />;
};

export default DefaultQuickActionsCard;

