import React from "react";
import AddHomeIcon from "@mui/icons-material/AddHome";
interface OccupancyRateCardProps {
  title?: string;
  occupiedUnits: number;
  totalUnits: number;
}

const OccupancyRateCard: React.FC<OccupancyRateCardProps> = ({
  title = "Tingkat Okupansi",
  occupiedUnits,
  totalUnits,
}) => {
  const percentage = Math.round((occupiedUnits / totalUnits) * 100);
  const availableUnits = totalUnits - occupiedUnits;

  // Simplified color logic
  const getColor = () => {
    if (percentage >= 90)
      return {
        bg: "bg-green-50",
        text: "text-green-600",
        ring: "stroke-green-500",
      };
    if (percentage >= 70)
      return {
        bg: "bg-orange-50",
        text: "text-orange-600",
        ring: "stroke-orange-500",
      };
    return { bg: "bg-red-50", text: "text-red-600", ring: "stroke-red-500" };
  };

  const color = getColor();
  const circumference = 2 * Math.PI * 45;
  const offset = circumference - (percentage / 100) * circumference;

  return (
    <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-4">
      {/* Header */}
      <div className="flex items-center gap-2 mb-4">
        <div className={`${color.bg} ${color.text} p-2 rounded-lg`}>
          <AddHomeIcon className="w-4 h-4" />
        </div>
        <h3 className="font-semibold text-gray-800 text-sm">{title}</h3>
      </div>

      {/* Progress Ring - Smaller */}
      <div className="flex flex-col items-center">
        <div className="relative w-24 h-24">
          <svg className="w-24 h-24 -rotate-90">
            <circle
              cx="48"
              cy="48"
              r="45"
              fill="none"
              stroke="#e5e7eb"
              strokeWidth="6"
            />
            <circle
              cx="48"
              cy="48"
              r="45"
              fill="none"
              className={color.ring}
              strokeWidth="6"
              strokeLinecap="round"
              strokeDasharray={circumference}
              strokeDashoffset={offset}
              style={{ transition: "stroke-dashoffset 0.5s ease" }}
            />
          </svg>
          <div className="absolute inset-0 flex items-center justify-center">
            <span className={`text-2xl font-bold ${color.text}`}>
              {percentage}%
            </span>
          </div>
        </div>

        {/* Stats */}
        <div className="grid grid-cols-3 gap-3 w-full mt-4">
          <div className="text-center">
            <p className="text-lg font-bold text-blue-600">{occupiedUnits}</p>
            <p className="text-xs text-gray-500">Terisi</p>
          </div>
          <div className="text-center">
            <p className="text-lg font-bold text-red-500">{availableUnits}</p>
            <p className="text-xs text-gray-500">Kosong</p>
          </div>
          <div className="text-center">
            <p className="text-lg font-bold text-gray-700">{totalUnits}</p>
            <p className="text-xs text-gray-500">Total</p>
          </div>
        </div>

        {/* Status Message */}
        <p className={`text-xs font-semibold mt-3 ${color.text}`}>
          {percentage >= 90
            ? "Okupansi sangat tinggi!"
            : percentage >= 70
            ? "Okupansi bagus"
            : "Perlu tingkatkan okupansi"}
        </p>
      </div>
    </div>
  );
};

// Demo
export default function App() {
  return (
    <div className="min-h-screen bg-gray-50 p-6">
      <div className="max-w-sm mx-auto">
        <OccupancyRateCard occupiedUnits={85} totalUnits={100} />
      </div>
    </div>
  );
}

