      {/* Grid Bento Box */}
      <Grid container spacing={1} sx={{ mb: 1 }}>
        {/* Baris 1: Statistik utama */}
        <Grid size={{ xs: 12, sm: 6, md: 3, lg: 3 }}>
          <StatCard
            title={statsDataWithIcons[0].title}
            value={statsDataWithIcons[0].value}
