            color={statsDataWithIcons[2].color}
            trend={statsDataWithIcons[2].trend}
            caption={statsDataWithIcons[2].caption || ""}
          />
        </Grid>
        <Grid size={{ xs: 12, sm: 6, md: 3, lg: 2 }}>
          <StatCard
            title={statsDataWithIcons[3].title}
            value={statsDataWithIcons[3].value}
